              ( 251, 41, 1, 'en-GB', 'Optimus Prime'),
              ( 252, 42, 1, 'en-GB', 'Optimus Prime. Leader of the heroic Autobots. And now an impressive 2-in-1 LEGO® build for Transformers fans. Awaken your passion for the Transformers universe with this rewarding building project for adults as you craft all the details of an iconic robot.'),
              ( 253, 41, 1, 'de-DE', 'Optimus Prime'),
